-- This script lists all genres in the database hbtn_0d_tvshows_rate by their rating.
select tv_genres.name, SUM(tv_show_ratings.rate) as rating from tv_shows INNER JOIN tv_show_ratings ON tv_show_ratings.show_id = tv_shows.id INNER JOIN tv_show_genres ON tv_shows.id = tv_show_genres.show_id INNER JOIN tv_genres ON tv_show_genres.genre_id = tv_genres.id GROUP by tv_genres.name ORDER by rating DESC;
